    rdfs:range :ManagementUnit .
:hasSupplier a owl:ObjectProperty ;
    rdfs:domain :Contract ;
    rdfs:range :Supplier .
:hasContract a owl:ObjectProperty ;
    rdfs:domain :PublicOrganization ;
    rdfs:range :Contract .
:hasContract a owl:ObjectProperty ;
    rdfs:domain :ManagementUnit ;
    rdfs:range :Contract .
:hasContract a owl:ObjectProperty ;
    rdfs:domain :Supplier ;
    rdfs:range :Contract .

